import React from "react";
import "../style.css";

export default function StoryBox({ title, text }) {
  let upperTitle = title[0].toUpperCase() + title.slice(1);

  /*  <input
            type="checkbox"
            id="about-during-box-toggle"
            className="story-box-toggle"
          />
          <label htmlFor="about-during-box-toggle">
            <div className="storybox-toggle-label">Present</div>
          </label> */
  return (
    <>
      <input
        type="checkbox"
        id={`about-${title}-box-toggle`}
        className="story-box-toggle"
      />

      <label htmlFor={`about-${title}-box-toggle`}>
        <div className="storybox-toggle-label">{upperTitle}</div>
      </label>
      <div id={`${title}`} className="about-story-box">
        {text}
      </div>
    </>
  );
}
