import React from "react";
import "./style.css";

export default function Modal({ visability, info, setState }) {
  function findE(e) {
    e.stopPropagation();
    if (e.target.id === "project-gallery" || e.target.id === "root") {
      setState();
    }
  }

  document.addEventListener("click", findE);

  return (
    <div
      onClick={findE}
      className={visability ? "modal-visable" : "modal-not-visable"}
      id="modal"
    >
      <button onClick={setState} className="modal-button">X</button>
      <h1>{info.title}</h1>
      <p>{info.desc}</p>

      {info.github ? (
        <p>
          <a href={info.link} target="_blank">
            View on GitHub
          </a>
        </p>
      ) : (
        <></>
      )}

      {info.livelink ? (
        <p>
          <a href={info.livelink} target="_blank">
            View Live
          </a>
        </p>
      ) : (
        <></>
      )}
    </div>
  );
}
