import React from "react";
import "./style.css";
import "../../../box-style.css";
import { socialsArr } from "../../../data/socials.js";
import emailjs from 'emailjs-com';



export default function Contact() {
  function sendEmail(e) {
    // e.preventDefault();

    emailjs.sendForm(process.env.EMAIL_JS_SERVICE_ID, process.env.EMAIL_JS_TEMPLATE_ID, e.target, process.env.EMAIL_JS_USER_ID)
      .then((result) => {
          console.log(result.text);

      }, (error) => {
          console.log(error.text);
      });
  }
  console.log({ socialsArr });
  return (
    <div className="anchor-box">
      <a name="contact" href="/#">
        &nbsp;
      </a>

      <div id="contact-container" className="box">
        <div id="contact-box">
          <h1 id="contact-h1">Get In Touch</h1>
          <p>
            I'm always happy to hear from people who love to code, and I'm
            excited about the new direction my life is taking. Get in touch via
            the following:
          </p>
          <ul>
            {socialsArr.map((item) => {
              if (item.title === "email" || item.title === "tel") {
                return <></>;
              } else {
                return (
                  <li>
                    <a href={item.link}>{item.title}</a>
                  </li>
                );
              }
            })}
          </ul>
          <h2>Or get in touch:</h2>
          <form className="contact-form" onSubmit={sendEmail}>
      <input type="hidden" name="contact_number" />
      <label>Name</label>
      <input type="text" name="user_name" />
      <label>Email</label>
      <input type="email" name="user_email" />
      <label>Message</label>
      <textarea name="message" />
      <input type="submit" value="Send" />
    </form>
        </div>
      </div>
    </div>
  );
}
