import React, { useState } from "react";
import "./style.css";
import "../../../gallery.css";
import projectArr from "../../../data/projects";
import Modal from "../../Modal";

import ProjectBox from "./ProjectBox";
export default function Projects() {
  const [modalView, setModalView] = useState(false);
  const [modalInfo, setModalInfo] = useState({});

  function handleClick(title, img, gif, desc, github, livelink) {
    setModalView(!modalView);
    setModalInfo({ title, img, gif, desc, github, livelink });
  }

  function closeModal() {
    if (modalView) {
      setModalView(false);
    } else {
      return;
    }
  }

  return (
    <div className="anchor-box">
      <a name="projects" href="/#">
        &nbsp;
      </a>

      <div id="project-container" className="box">
        <h2>Coding Projects</h2>

        <Modal visability={modalView} info={modalInfo} setState={closeModal} />

        <div id="project-box">
          <div id="project-gallery" className="misc-gallery">
            {projectArr.map((a) => {
              return (
                <div className="smol-project-box">
                  <h3>{a.title}</h3>
                  <img
                    src={a.img}
                    alt={a.title}
                    onClick={handleClick}
                  />
                </div>
              );
            })}
          </div>
        </div>
      </div>
    </div>
  );
}


