import React, { useState } from "react";
import "./style.css";
import "../../../gallery.css";
import projectArr from "../../../data/projects";
import Modal from "../../Modal"

import ProjectBox from "./ProjectBox";
export default function Projects() {
  const [modalView, setModalView] = useState(false);
  const [modalInfo, setModalInfo] = useState("");
  console.log(projectArr)

  function handleClick(e) {
    setModalView(!modalView);
    setModalInfo(e.target.alt)
  }
  return ( 
    
    <div className="anchor-box">
      <a name="projects" href="/#">
        &nbsp;
      </a>

      <div id="project-container" className="box">
        <h2>Coding Projects</h2>
    <Modal visability={modalView} modalInfo={modalInfo}/>
        <div id="project-box">
          <div id="project-gallery" className="misc-gallery">

            {projectArr.map((a) => {
              return (
                <ProjectBox
                  title={a.title}
                  imgSrc={a.src}
                  desc={a.desc}
                  link={a.link}
                  liveLink={a.liveLink}
                  handleClick={handleClick}
                />
              );
            })}
          </div>
        </div>
      </div>
    </div>
  );
}
