import React from "react";
import "../style.css"

export default function StoryBox({ title, text }) {
  let upperTitle = title[0].toUpperCase();
  console.log(upperTitle)

  return (
    <>
      <input
        type="checkbox"
        id={`about-${title}-box-toggle`}
        className="story-box-toggle"
      />

      <label htmlFor="about-before-box-toggle">
        <div className="storybox-toggle-label">{title}</div>
      </label>
      <div id={title} className="about-story-box">
        {text}
      </div>
    </>
  );
}
