import React, {useState} from "react";
import Modal from "../../../Modal"

export default function ProjectBox(props) {
  const [modalView, setModalView] = useState(false);
  function handleClick(){
    setModalView(!modalView);
  }
  console.log(modalView)
  return (
    <div className="smol-project-box">
      <h3>{props.title}</h3>
      <img src={props.imgSrc} alt={props.title} onClick={handleClick}/>

      {/* 
        When image is clicked, return a modal that has the below information
       */}

      <a href={props.liveLink}></a>
      <p>{props.desc}</p>

      <p>
        {props.liveLink ? (
          <>
            <a href={props.liveLink}>View Live</a>
            <br />
          </>
        ) : (
          <></>
        )}

        {props.link ? <a href={props.link}>View On GitHub</a> : <></>}
      </p>
    </div>
  );
}
