import React from "react";
import "./style.css";

export default function Modal({ visability, info, setState }) {

    function findE (e){
        if(e!=document){
            return
        }
        else{
            setState()
        }
console.log(e.target)
        // document.addEventListener("click", setState)
    }



  return (
    <div  onClick={findE} className={visability 
    ? "modal-visable" 
    : "modal-not-visable"}
    id="modal">

    <button onClick={setState}>X</button>
    <h1>{info.title}</h1>
    <p>{info.desc}</p>

        {info.github ? 
        <p><a href={info.link} target="_blank">View on GitHub</a></p>  : <></>
        }

        {info.livelink ? 
        <p><a href={info.livelink} target="_blank">View Live</a></p>  : <></>
        }
      

    
    </div>
  );
}
