import React, { useState } from "react";
import "./style.css";
import "../../../gallery.css";
import projectArr from "../../../data/projects";
import Modal from "../../Modal";

import ProjectBox from "./ProjectBox";
export default function Projects() {
  const [modalView, setModalView] = useState(false);
  const [modalInfo, setModalInfo] = useState({});

  function handleClick(title, img, desc, github, livelink) {
    setModalView(!modalView);
    setModalInfo({ title, img, desc, github, livelink });
  }

  function closeModal(){
    setModalView(false)
  }

  document.addEventListener("click", closeModal)


  console.log(modalInfo);

  return (
    <div className="anchor-box">
      <a name="projects" href="/#">
        &nbsp;
      </a>

      <div id="project-container" className="box">
        <h2>Coding Projects</h2>

    <Modal visability={modalView} info={modalInfo}/>

        <div id="project-box">
          <div id="project-gallery" className="misc-gallery">
            {projectArr.map((a) => {
              return (
                <>
                  <ProjectBox
                    title={a.title}
                    imgSrc={a.src}
                    handleClick={() => {
                      handleClick(a.title, a.src, a.desc, a.link, a.liveLink);
                    }}
                  />
                </>
              );
            })}
          </div>
        </div>
      </div>
    </div>
  );
}
